import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const [properties, setProperties] = useState([]);
  const [newProperty, setNewProperty] = useState({
    name: '',
    type: '',
    address: '',
    purchaseDate: '',
    image: null,
  });

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/properties', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setProperties(res.data);
      } catch (err) {
        console.error(err);
        alert('Failed to fetch properties');
      }
    };
    fetchProperties();
  }, []);

  const handlePropertyChange = (e) => {
    const { name, value } = e.target;
    setNewProperty({
      ...newProperty,
      [name]: value,
    });
  };

  const handlePropertySubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', newProperty.name);
    formData.append('type', newProperty.type);
    formData.append('address', newProperty.address);
    formData.append('purchaseDate', newProperty.purchaseDate);
    formData.append('image', newProperty.image);

    try {
      await axios.post('http://localhost:5000/api/properties', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      setNewProperty({ name: '', type: '', address: '', purchaseDate: '', image: null });
      alert('Property added successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to add property');
    }
  };

  return (
    <div>
      <h2>Welcome to the Dashboard</h2>
      <button onClick={() => navigate('/login')}>Logout</button>

      <h3>Add New Property</h3>
      <form onSubmit={handlePropertySubmit}>
        <input
          type="text"
          name="name"
          value={newProperty.name}
          onChange={handlePropertyChange}
          placeholder="Property Name"
          required
        />
        <select name="type" value={newProperty.type} onChange={handlePropertyChange} required>
          <option value="">Select Property Type</option>
          <option value="House">House</option>
          <option value="Apartment">Apartment</option>
          <option value="Commercial">Commercial</option>
        </select>
        <input
          type="text"
          name="address"
          value={newProperty.address}
          onChange={handlePropertyChange}
          placeholder="Address"
          required
        />
        <input
          type="date"
          name="purchaseDate"
          value={newProperty.purchaseDate}
          onChange={handlePropertyChange}
          required
        />
        <input
          type="file"
          name="image"
          onChange={(e) => setNewProperty({ ...newProperty, image: e.target.files[0] })}
        />
        <button type="submit">Add Property</button>
      </form>

      <h3>Your Properties</h3>
      <ul>
        {properties.map((property) => (
          <li key={property.id}>
            {property.name} - {property.type} - {property.address}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
