import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ServiceManagement = () => {
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    type: '',
    date: '',
    cost: '',
    propertyId: '',
  });

  const [properties, setProperties] = useState([]);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/services', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setServices(res.data);
      } catch (err) {
        console.error(err);
        alert('Failed to fetch services');
      }
    };
    fetchServices();

    const fetchProperties = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/properties', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        setProperties(res.data);
      } catch (err) {
        console.error(err);
        alert('Failed to fetch properties');
      }
    };
    fetchProperties();
  }, []);

  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService({
      ...newService,
      [name]: value,
    });
  };

  const handleServiceSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/services', newService, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      alert('Service added successfully');
    } catch (err) {
      console.error(err);
      alert('Failed to add service');
    }
  };

  return (
    <div>
      <h2>Service Management</h2>
      <form onSubmit={handleServiceSubmit}>
        <select name="type" value={newService.type} onChange={handleServiceChange} required>
          <option value="">Select Service Type</option>
          <option value="Plumbing">Plumbing</option>
          <option value="Electrical">Electrical</option>
          <option value="General Maintenance">General Maintenance</option>
        </select>
        <input
          type="date"
          name="date"
          value={newService.date}
          onChange={handleServiceChange}
          required
        />
        <input
          type="number"
          name="cost"
          value={newService.cost}
          onChange={handleServiceChange}
          placeholder="Cost"
          required
        />
        <select
          name="propertyId"
          value={newService.propertyId}
          onChange={handleServiceChange}
          required
        >
          <option value="">Select Property</option>
          {properties.map((property) => (
            <option key={property.id} value={property.id}>
              {property.name}
            </option>
          ))}
        </select>
        <button type="submit">Add Service</button>
      </form>

      <h3>Services</h3>
      <ul>
        {services.map((service) => (
          <li key={service.id}>
            {service.type} - {service.date} - {service.cost} - {service.property.name}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ServiceManagement;
