import React, { useState } from "react";
import "./PropertyListPage.css";

const PropertyManager = () => {
  const [properties, setProperties] = useState([]);
  const [formData, setFormData] = useState({
    PropertyName: "",
    PropertyType: "",
    Address: "",
    DateOfPurchase: "",
    propertyImage: "",
  });
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [showFormPopup, setShowFormPopup] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [deleteIndex, setDeleteIndex] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    setFormData({ ...formData, propertyImage: URL.createObjectURL(e.target.files[0]) });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isEditing) {
      const updatedProperties = [...properties];
      updatedProperties[editingIndex] = formData;
      setProperties(updatedProperties);
      setIsEditing(false);
      setEditingIndex(null);
    } else {
      setProperties([...properties, formData]);
    }
    setFormData({
      PropertyName: "",
      PropertyType: "",
      Address: "",
      DateOfPurchase: "",
      propertyImage: "",
    });
    setShowFormPopup(false);
  };

  const handleEdit = (index) => {
    setFormData(properties[index]);
    setIsEditing(true);
    setEditingIndex(index);
    setShowFormPopup(true);
  };

  const handleDelete = (index) => {
    setShowDeleteConfirmation(true);
    setDeleteIndex(index);
  };

  const confirmDelete = () => {
    const updatedProperties = properties.filter((_, i) => i !== deleteIndex);
    setProperties(updatedProperties);
    setShowDeleteConfirmation(false);
    setDeleteIndex(null);
  };

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const handleUpdateListClick = () => {
    if (isLoggedIn) {
      setShowFormPopup(true);
    } else {
      window.location.href = "/login"; // Replace with your login route
    }
  };

  return (
    <div className="property-manager">
      <h1>Property Manager</h1>
      <button onClick={handleUpdateListClick} className="update-list-btn">
        Update List
      </button>

      {isLoggedIn ? (
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      ) : (
        <button onClick={handleLogin} className="login-btn">Login</button>
      )}

      <div className="queue-cards">
        {properties.map((property, index) => (
          <div key={index} className="queue-card">
            <img src={property.propertyImage} alt={property.PropertyName} />
            <h3>{property.PropertyName}</h3>
            <p>Type: {property.PropertyType}</p>
            <p>Address: {property.Address}</p>
            <p>Date of Purchase: {property.DateOfPurchase}</p>
            {isLoggedIn && (
              <>
                <button onClick={() => handleEdit(index)}>Edit</button>
                <button onClick={() => handleDelete(index)}>Delete</button>
              </>
            )}
          </div>
        ))}
      </div>

      {showFormPopup && (
        <div className="form-popup">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="PropertyName"
              placeholder="Property Name"
              value={formData.PropertyName}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="PropertyType"
              placeholder="Property Type"
              value={formData.PropertyType}
              onChange={handleChange}
              required
            />
            <input
              type="text"
              name="Address"
              placeholder="Address"
              value={formData.Address}
              onChange={handleChange}
              required
            />
            <input
              type="date"
              name="DateOfPurchase"
              placeholder="Date of Purchase"
              value={formData.DateOfPurchase}
              onChange={handleChange}
              required
            />
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              required={!isEditing}
            />
            <button type="submit">{isEditing ? "Update Property" : "Add Property"}</button>
            <button type="button" onClick={() => setShowFormPopup(false)}>Close</button>
          </form>
        </div>
      )}

      {showDeleteConfirmation && (
        <div className="delete-confirmation">
          <p>Do you really want to delete this property?</p>
          <button onClick={confirmDelete}>Yes</button>
          <button onClick={() => setShowDeleteConfirmation(false)}>No</button>
        </div>
      )}
    </div>
  );
};

export default PropertyManager;
