import React, { useState, useEffect } from 'react';
import API from './api';

const PropertyManagement = () => {
  const [properties, setProperties] = useState([]);
  const [newProperty, setNewProperty] = useState({
    name: '',
    type: 'House',
    address: '',
    purchaseDate: '',
    image: null,
  });

  useEffect(() => {
    fetchProperties();
  }, []);

  const fetchProperties = async () => {
    try {
      const { data } = await API.get('/properties');
      setProperties(data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleChange = (e) => {
    setNewProperty({ ...newProperty, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setNewProperty({ ...newProperty, image: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    Object.entries(newProperty).forEach(([key, value]) => formData.append(key, value));

    try {
      await API.post('/properties', formData);
      fetchProperties();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div>
      <h2>Property Management</h2>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Property Name" onChange={handleChange} required />
        <select name="type" onChange={handleChange}>
          <option value="House">House</option>
          <option value="Apartment">Apartment</option>
          <option value="Commercial">Commercial</option>
        </select>
        <input name="address" placeholder="Address" onChange={handleChange} required />
        <input type="date" name="purchaseDate" onChange={handleChange} required />
        <input type="file" onChange={handleFileChange} required />
        <button type="submit">Add Property</button>
      </form>
      <ul>
        {properties.map((property) => (
          <li key={property.id}>{property.name} ({property.type})</li>
        ))}
      </ul>
    </div>
  );
};

export default PropertyManagement;
